mapper.insert:
INSERT INTO boardtable (boardtable_seq, nickname, email, subject, content, image, ref, step) 
values(boardtable_seq.nextval
    , #{nickname}
    , #{email}
    , #{subject}
    , #{content}
    , #{image}
    , boardtable_seq.currval
    , '0')

mapper.select:

select * from
(select rownum rn, tt.* from
(select * from boardtable where step=0 order by boardtable_seq desc)tt 
)where rn>=#{startNum} and rn<=#{endNum}


select * from
(select rownum rn, tt.* from
(select * from boardtable where ${searchOption} like '%'||#{keyword}||'%' and step = 0
                order by ref desc, step asc)tt
)where rn>=#{startNum} and rn<=#{endNum}

mapper.update:
UPDATE boardtable SET subject=#{subject}
                ,content=#{content}
                ,logtime=sysdate
WHERE boardtable_seq=#{boardtable_seq}

mapper.insert:
insert into boardtable(boardtable_seq, nickname, email, content, logtime, ref, step, pseq, image) 
	values(boardtable_seq.nextval
	     , #{nickname }
	     , #{email}
	     , #{content}
	     , sysdate
	     , #{ref}
	     , #{step}
	     , #{pseq}
	     , #{image})
